// reducers.js
import { CHANGE_SCREEN } from './actions';

const initialState = {
  currentScreen: 'intro', // Initial screen
};

const screenReducer = (state = initialState, action) => {
  switch (action.type) {
    case CHANGE_SCREEN:
      return {
        ...state,
        currentScreen: action.payload,
      };
    default:
      return state;
  }
};

export default screenReducer;
