// reducers.js
const initialState = {
  quizState: 'intro', // Initial state for the quiz
  gameState: 'intro', // Initial state for the game
  score: 0, // Initial score
  questions: [], // Initial empty array for questions
  currQuestionIndex: 0, // Initial index for the current question
  // Add more initial state properties as needed...
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'SET_QUIZ_STATE':
      return {
        ...state,
        quizState: action.payload,
      };
    case 'SET_GAME_STATE':
      return {
        ...state,
        gameState: action.payload,
      };
    case 'SET_SCORE':
      return {
        ...state,
        score: state.score + 1, // Increment score by 1, adjust as needed
      };
    // Add more cases to handle different actions...

    default:
      return state;
  }
};
