// QuizScreen.js
import React from "react";
import { connect } from "react-redux";
import { startQuiz, nextQuestion, submitAnswer } from "../Redux/actions";

function QuizScreen({
  uestions,
  currentQuestionIndex,
  quizStarted,
  userAnswers,
  startQuiz,
  nextQuestion,
  submitAnswer,
}) {
  const startQuizHandler = () => {
    startQuiz();
  };

  const nextQuestionHandler = () => {
    nextQuestion();
  };

  const submitAnswerHandler = (answer) => {
    submitAnswer(answer);
  };

  if (!quizStarted) {
    return (
      <div>
        <button onClick={startQuizHandler}>Start Quiz</button>
      </div>
    );
  }

  if (currentQuestionIndex < questions.length) {
    const currentQuestion = questions[currentQuestionIndex];

    return (
      <div>
        <h3>{currentQuestion.text}</h3>
        <ul>
          {currentQuestion.options.map((option) => (
            <li key={option.id}>
              <button onClick={() => submitAnswerHandler(option)}>
                {option.text}
              </button>
            </li>
          ))}
        </ul>
        <button onClick={nextQuestionHandler}>Next</button>
      </div>
    );
  }

  return <div>Quiz Finished!</div>;
}

const mapStateToProps = (state) => ({
  questions: state.quiz.questions,
  currentQuestionIndex: state.quiz.currentQuestionIndex,
  userAnswers: state.quiz.userAnswers,
  quizStarted: state.quiz.quizStarted,
});

const mapDispatchToProps = {
  startQuiz,
  nextQuestion,
  submitAnswer,
};

export default connect(mapStateToProps, mapDispatchToProps)(QuizScreen);
