import React from 'react';
import "./App.css";
import { Provider } from 'react-redux';
import store from './Redux/store';
import IntroScreen from "./components/IntroScreen";
import QuizScreen from "./components/QuizScreen";
import EndScreen from "./components/EndScreen";
import GameScreen from "./components/GameScreen";

function App() {
  return (
    <Provider store={store}>
      <div className="App">
        <h1>DeepFake App</h1>
        {/* Render different screens/components based on Redux state */}
        {renderAppContent()}
      </div>
    </Provider>
  );
}

function renderAppContent() {
  // Logic to render different screens based on Redux state
  switch (store.getState().gameState) {
    case 'menu':
      return <IntroScreen />;
    case 'qui':
      return <QuizScreen />;
    case 'game':
      return <GameScreen />;
    case 'end':
      return <EndScreen />;
    default:
      return null;
  }
}

export default App;
