import React from "react";
import { useNavigate } from "react-router-dom";
import { resetGame } from "../../Redux/actions";
import { useEffect } from "react";

function ResetButton() {
    const navigate = useNavigate();
    const reset = () => {
        resetGame();
    };

    const changeScreen = () => useEffect(() => {
        navigate("/game");
      });

    return (
        <div>
            <button onClick={reset()} className="reset-button" type="reset" value="Reset" 
            style={{
                backgroundColor: "black",
                color: "white",
                border: "none",
                borderRadius: "5px",
                padding: "10px 20px",
                fontSize: "16px",
                cursor: "pointer",
                margin: "10px",
                }}
            >Reset</button>
        </div>
    );
}

export default ResetButton;
