import { CHANGE_SCREEN, CHANGE_SCORE, RESET_GAME } from './actions';

// setting initial state
const initialState = {
  currentScreen: '/intro',
  score: 0,
};

const quizReducer = (state = initialState, action) => {
  switch (action.type) {
    case CHANGE_SCREEN:
      return {
        ...state,
        currentScreen: action.payload, // Update current screen
      };
    case CHANGE_SCORE:
      return {
        ...state,
        score: action.payload, // Update score
      };
    case RESET_GAME:
      return {
        ...state,
        score: 0,
        currentScreen: navigate("/intro"), // Reset score and go back to intro screen
      }
    default:
      return state;
  }
};

export default quizReducer;
