import React from "react";
import { connect } from 'react-redux';
import { setGameState } from "../Redux/actions";


function IntroScreen({ stGameState}) {
    return (
        <div className="intro-screen">
            <h1>Welcome to the game!</h1>
            <button onClick={() => setGameState("play")}>Start Quiz</button>
<button onClick={() => setGameState("game")}>Play Real or Not Real</button>
        </div>
    );
}

const mapDispatchToProps = dispatch => ({
    setGameState: (newState) => dispatch(setGameState(newState)),
});

export default connect(null, mapDispatchToProps)(IntroScreen);
