import React from "react";
import { connect } from "react-redux";
import { setGameState } from "../Redux/actions"; // Action to set game state

function ResultScreen({ score, setGameState }) {
    const handleContinue = () => {
      setGameState("game"); // Change game state to continue playing
    };
  
    const handlePlayAgain = () => {
      setGameState("menu"); // Change game state to play again
    };

    return (
        <div className="result-screen">
            <h2>Your Score: {score}</h2>
            <button onClick={handleContinue}>Continue</button>
            <button onClick={handlePlayAgain}>Play Again</button>
        </div>
    );
}

const mapStateToProps = (state) => ({
    score: state.score, // Retrieve score from Redux state
  });
  
  const mapDispatchToProps = (dispatch) => ({
    setGameState: (newState) => dispatch(setGameState(newState)),
    // Other actions if needed...
  });
  
  export default connect(mapStateToProps, mapDispatchToProps)(ResultScreen);
