import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { changeScreen, changeScore } from '../Redux/actions';
import Questions from '../Redux/Questions';

function QuizScreen() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const dispatch = useDispatch();

  const currentQuestion = Questions[currentQuestionIndex];

// Logic to calculate score
const calculateScore = () => {
  // ...calculate score logic based on quiz completion
  const userScore = Math.floor(score + 1); // Replace this with your score calculation logic

  // Dispatch action to update score in Redux store
  dispatch(updateScore(userScore));
};

  const handleAnswerSelection = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }
  };

  const handleNextQuestion = () => {
    if (currentQuestionIndex < Questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      dispatch(changeScreen('result'));
    }
  };

  const handleGoBack = () => {
    dispatch(changeScreen('menu'));
  };

  const handleCheckAnswerAndNext = () => {
    const currentAnswer = Questions[currentQuestionIndex];
    if (currentAnswer && currentAnswer.options.some((option) => option.isCorrect)) {
      dispatch(changeScore(1));
    }
    handleNextQuestion();
  };

  return (
    <div className="quiz-screen">
      <h2>Quiz Screen</h2>
      {currentQuestion && (
        <div>
          <h3>{currentQuestion.text}</h3>
          <ul>
            {currentQuestion.options.map((option) => (
              <li key={option.id}>
                <button onClick={() => handleAnswerSelection(option.isCorrect)}>
                  {option.text}
                </button>
              </li>
            ))}
          </ul>
          <button onClick={handleCheckAnswerAndNext}>Next Question</button>
        </div>
      )}
      <p>Current Score: {score}</p>
      <button onClick={handleGoBack}>Go Back</button>
    </div>
  );
}

export default QuizScreen;
