import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { changeScreen } from '../Redux/actions';
import { RestartButton } from './components/RestartButton';

function ResultScreen() {
  const score = useSelector((state) => state.score);
  const dispatch = useDispatch();

  const handleRestart = () => dispatch(changeScreen('quiz')); // Navigate to the quiz screen on restart
  const handleStartGame = () => dispatch(changeScreen('game')); // Navigate to the game screen on handleStartGame


  return (
    <div className="result-screen">
      <h2>Result Screen</h2>
      <p>Final Score: {score}</p>
      <button onClick={handleRestart}>Restart Quiz</button>
      <button onClick={handleStartGame}>Start Game</button>
      <RestartButton />
    </div>
  );
}

export default ResultScreen;
