// reducers.js
import { START_QUIZ, NEXT_QUESTION, SUBMIT_ANSWER } from './actions';

const initialState = {
  questions: [], // Populate this with your set of questions
  currentQuestionIndex: 0,
  userAnswers: [],
  quizStarted: false,
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_CURRENT_QUESTION_INDEX:
      return {
        ...state,
        currentQuestionIndex: action.payload,
      };
    case INCREMENT_SCORE:
      return {
        ...state,
        score: state.score + 1,
      };
    case CHANGE_SCREEN:
      return {
        ...state,
        currentScreen: action.payload,
      };
    // Add other cases if needed for your app's functionality...

    default:
      return state;
  }
};

export default rootReducer;
