import { CHANGE_SCREEN, CHANGE_SCORE, RESET_GAME } from './actions';


const initialState = {
  currentScreen: 'intro',
  score: 0, // Initialize score to zero
};

const quizReducer = (state = initialState, action) => {
  switch (action.type) {
    case CHANGE_SCREEN:
      return {
        ...state,
        currentScreen: action.payload,
      };
    case CHANGE_SCORE:
      return {
        ...state,
        score: action.payload, // Update score
      };
    case RESET_GAME:
      return {
        ...state,
        score: 0,
        currentScreen: 'intro',
      }
    default:
      return state;
  }
};

export default quizReducer;
