import { CHANGE_SCREEN, INCREMENT_SCORE } from './actions';


const initialState = {
  currentScreen: 'intro',
  score: 0, // Initialize score to zero
};

const quizReducer = (state = initialState, action) => {
  switch (action.type) {
    case CHANGE_SCREEN:
      return {
        ...state,
        currentScreen: action.payload,
      };
    case 'INCREMENT_SCORE':
      return {
        ...state,
        score: state.score + 1,
      };
    default:
      return state;
  }
};

export default quizReducer;
