import React from "react";
import { connect } from "react-redux";
import { changeScreen } from "../Redux/actions";
import { useNavigate } from "react-router-dom";

function IntroScreen({ changeScreen }) {
  
  const handlePlayQuiz = () => {
    navigate("/home")
    changeScreen("quiz"); // Dispatch changeScreen action with "quiz" screen
  };

  const handlePlayGame = () => {
    changeScreen("game"); // Dispatch changeScreen action with "game" screen
  };

  return (
    <div className="intro-menu">
      <h1>Information</h1>
      <button onClick={handlePlayQuiz}>Play Quiz</button>
      <button onClick={handlePlayGame}>Play Real or Not Real</button>
    </div>
  );
}

const mapDispatchToProps = (dispatch) => ({
  changeScreen: (screen) => dispatch(changeScreen(screen)),
});

export default connect(null, mapDispatchToProps)(IntroScreen);
