// components/QuizScreen.js
import React from 'react';
import { connect } from 'react-redux';
import { setCurrentQuestionIndex, incrementScore, changeScreen } from '../Redux/actions';

function QuizScreen({
  questions,
  currentQuestionIndex,
  score,
  setCurrentQuestionIndex,
  incrementScore,
  changeScreen,
}) {
  const handleOptionClick = (selectedOption) => {
    const correctAnswer = questions[currentQuestionIndex].options.find(
      (option) => option.isCorrect
    );

    if (selectedOption === correctAnswer.text) {
      incrementScore();
    }

    // Logic to handle other aspects of option selection

    // Move to the next question or end the quiz
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      // End the quiz, navigate to result screen or handle as needed
      changeScreen('end');
    }
  };

  if (!questions || questions.length === 0) {
    return <div>Loading...</div>; // Handle loading state
  }

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="quiz-screen">
      <h2>Question {currentQuestionIndex + 1} of {questions.length}</h2>
      <h3>{currentQuestion.text}</h3>
      <div className="options">
        {currentQuestion.options.map((option) => (
          <div
            key={option.id}
            className="option"
            onClick={() => handleOptionClick(option.text)}
          >
            <p>{option.text}</p>
          </div>
        ))}
      </div>
      <p>Progress: {currentQuestionIndex + 1} / {questions.length}</p>
      <p>Score: {score}</p>
    </div>
  );
}

const mapStateToProps = (state) => ({
  questions: state.questions,
  currentQuestionIndex: state.currentQuestionIndex,
  score: state.score,
});

const mapDispatchToProps = {
  setCurrentQuestionIndex,
  incrementScore,
  setGameState,
};

export default connect(mapStateToProps, mapDispatchToProps)(QuizScreen);
