// Redux/reducers.js

import { SET_GAME_STATE } from './actions';

const initialState = {
  gameState: 'menu', // Initial game state
  currQuestion: 0,
  score: 0,
  selectedOption: null,
};


const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_GAME_STATE:
      return {
        ...state,
        gameState: action.payload,
      };
    // Handle other actions if needed...
    default:
      return state;
  }
};

export default rootReducer;

