import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';
import { changeScreen, changeScore } from '../Redux/actions';
import Questions from '../Redux/Questions';

function QuizScreen() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  // const [score, setScore] = useState(0);
  const dispatch = useDispatch();
  const score = useSelector(state => state.score)
  const currentQuestion = Questions[currentQuestionIndex];

  const handleAnswerSelection = (isCorrect) => {
    if (isCorrect) {
      // Increase the score if the answer is correct
      setScore(score + 1);
    }
  };

  const handleNextQuestion = () => {
    // Check if the current answer was correct and then update the score
    const currentAnswer = Questions[currentQuestionIndex];
    if (currentAnswer && currentAnswer.options.some((option) => option.isCorrect)) {
      dispatch(changeScore(score + 1)); // Dispatch the action to increment the score
    }

    // Move to the next question
    if (currentQuestionIndex < Questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      // If all questions answered, move to the result screen
      dispatch(changeScreen('result')); // Change to result screen when all questions are answered
    }
  };

  const handleGoBack = () => {
    dispatch(changeScreen('menu')); // Dispatch changeScreen action with "intro" screen
  };

  return (
    <div className="quiz-screen">
      <h2>Quiz Screen</h2>
      {currentQuestion && (
        <div>
          <h3>{currentQuestion.text}</h3>
          <ul>
            {currentQuestion.options.map((option) => (
              <li key={option.id}>
                <button
                  onClick={() => {
                    handleAnswerSelection(option.isCorrect);
                  }}
                >
                  {option.text}
                </button>
              </li>
            ))}
          </ul>
          <button onClick={handleNextQuestion}>Next Question</button>
        </div>
      )}
      <p>Current Score: {score}</p>
      <button onClick={handleGoBack}>Go Back</button>
    </div>
  );
}

export default QuizScreen;
