import { CHANGE_SCREEN, CHANGE_SCORE } from './actions';


const initialState = {
  currentScreen: 'intro',
  score: 0, // Initialize score to zero
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case CHANGE_SCREEN:
      return {
        ...state,
        currentScreen: action.payload,
      };
    case CHANGE_SCORE:
      return {
        ...state,
        score: state.score + action.payload,
      };
    default:
      return state;
  }
};

export default rootReducer;
