// actions.js
export const CHANGE_SCREEN = 'CHANGE_SCREEN';

export const changeScreen = (screen) => ({
  type: CHANGE_SCREEN,
  payload: screen,
});
export const setScore = () => ({
    type: 'SET_SCORE',
    // You might pass a payload if needed, like the score value
    // payload: newScore,
  });
