// actions.js
export const CHANGE_SCREEN = 'CHANGE_SCREEN';
export const CHANGE_SCORE = 'INCREMENT_SCORE';

export const changeScreen = (screen) => ({
  type: CHANGE_SCREEN,
  payload: screen,
});

export const incrementScore = () => ({
  type: INCREMENT_SCORE,
});
