import React from 'react';
import { connect } from 'react-redux';
import IntroScreen from '../components/IntroScreen';
import QuizScreen from './components/QuizScreen';
import GameScreen from './components/GameScreen';
import ResultScreen from '../components/ResultScreen';
import { changeScreen } from './Redux/actions';

function App({ currentScreen, changeScreen }) {
  const renderScreen = () => {
    switch (currentScreen) {
      case 'intro':
        return <IntroScreen />;
      case 'quiz':
        return <QuizScreen />;
      case 'game':
        return <GameScreen />;
      case 'result':
        return <ResultScreen />;
      default:
        return <IntroScreen />;
    }
  };

  return (
    <div className="App">
      {renderScreen()}
      {/* Example buttons to change screens */}
      <button onClick={() => changeScreen('intro')}>Go to Intro</button>
      <button onClick={() => changeScreen('quiz')}>Go to Quiz</button>
      <button onClick={() => changeScreen('game')}>Go to Game</button>
      <button onClick={() => changeScreen('result')}>Go to Result</button>
    </div>
  );
}

const mapStateToProps = (state) => ({
  currentScreen: state.currentScreen,
});

const mapDispatchToProps = (dispatch) => ({
  changeScreen: (screen) => dispatch(changeScreen(screen)),
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
