const initialState = {
  currentScreen: 'intro',
  score: 0, // Initialize score to zero
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case CHANGE_SCREEN:
      return {
        ...state,
        currentScreen: action.payload,
      };
    case CHANGE_SCORE:
      return {
        ...state,
        score: action.payload, // Update to action.payload instead of action.score
      };
    default:
      return state;
  }
};

export default rootReducer;
