import React, { useState } from 'react';
import {useScore } from '../Utils/';
import { connect } from 'react-redux';
import { changeScreen, incrementScore } from '../Redux/actions';
import Questions from '../Redux/Questions';

function QuizScreen({ changeScreen }) {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);

  const currentQuestion = Questions[currentQuestionIndex];

  const handleAnswerSelection = (isCorrect) => {
    if (isCorrect) {
      // Increase the score if the answer is correct
      setScore(score + 1);
    }
  };

  const handleNextQuestion = () => {
    // Check if the current answer was correct and then update the score
    const currentAnswer = Questions[currentQuestionIndex];
    if (currentAnswer && currentAnswer.options.some((option) => option.isCorrect)) {
      incrementScore(); // Dispatch the action to increment the score
    }
  
    // Move to the next question
    if (currentQuestionIndex < Questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      // If all questions answered, move to the result screen
      changeScreen('result'); // Change to result screen when all questions are answered
    }
  };

  const handleGoBack = () => {
    changeScreen('menu'); // Dispatch changeScreen action with "intro" screen
  };

  return (
    <div className="quiz-screen">
      <h2>Quiz Screen</h2>
      {currentQuestion && (
        <div>
          <h3>{currentQuestion.text}</h3>
          <ul>
            {currentQuestion.options.map((option) => (
              <li key={option.id}>
                <button
                  onClick={() => {
                    handleAnswerSelection(option.isCorrect);
                  }}
                >
                  {option.text}
                </button>
              </li>
            ))}
          </ul>
          <button onClick={handleNextQuestion}>Next Question</button>
        </div>
      )}
      <p>Current Score: {score}</p>
      <button onClick={handleGoBack}>Go Back</button>
    </div>
  );
}

const mapDispatchToProps = (dispatch) => ({
  changeScreen: (screen) => dispatch(changeScreen(screen)),
  incrementScore: () => dispatch(incrementScore()), // Add this line
});

export default connect(null, mapDispatchToProps)(QuizScreen);
