// Redux Actions

//Define action types
export const SET_GAME_STATE = 'SET_GAME_STATE';

//Action creators
export const setGameState = (gameState) => ({
    type: SET_GAME_STATE,
    payload: gameState,
});

export const SET_SCORE = 'SET_SCORE';

export const setScore = () => ({
  type: SET_SCORE,
  // You might pass any necessary payload for setting the score
});
