import { CHANGE_SCREEN, CHANGE_SCORE, RESET } from './actions';


const initialState = {
  currentScreen: 'intro',
  score: 0, // Initialize score to zero
};

const quizReducer = (state = initialState, action) => {
  switch (action.type) {
    case CHANGE_SCREEN:
      return {
        ...state,
        currentScreen: action.payload,
      };
    case CHANGE_SCORE:
      return {
        ...state,
        score: action.payload, // Update to action.payload instead of action.score
      };
    case 'RESET_SCORE':
      return {
        ...state,
        score: 0, // Reset score to zero
        currentScreen: 'intro', // Reset current screen to 'intro'
      }
    default:
      return state;
  }
};

export default quizReducer;
