// Redux/reducers.js
import {
  SET_CURRENT_QUESTION_INDEX,
  INCREMENT_SCORE,
  SET_GAME_STATE,
  CHANGE_SCREEN,
} from './actions';

const initialState = {
  questions: [], // Add your questions array here
  currentQuestionIndex: 0,
  score: 0,
  gameState: 'menu', // Initial game state
  currentScreen: 'intro', // Initial screen
  // Other initial state properties...
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_CURRENT_QUESTION_INDEX:
      return {
        ...state,
        currentQuestionIndex: action.payload,
      };
    case INCREMENT_SCORE:
      return {
        ...state,
        score: state.score + 1,
      };
    case SET_GAME_STATE:
      return {
        ...state,
        gameState: action.payload,
      };
    case CHANGE_SCREEN:
      return {
        ...state,
        currentScreen: action.payload,
      };
    // Add other cases if needed for your app's functionality...

    default:
      return state;
  }
};

export default rootReducer;
