export const SET_CURRENT_SCREEN = 'SET_CURRENT_SCREEN';

export const setCurrentScreen = (screen) => ({
  type: SET_CURRENT_SCREEN,
  payload: screen,
});
  
export const setScore = () => ({
    type: 'SET_SCORE',
    // You might pass a payload if needed, like the score value
    // payload: newScore,
  });
