import { combineReducers } from 'redux';
import { SET_GAME_STATE, SET_QUIZ_STATE } from './actions';

// Reducer for game state
const gameStateReducer = (state = 'game', action) => {
  switch (action.type) {
    case SET_GAME_STATE:
      return action.payload;
    default:
      return state;
  }
};

// Reducer for quiz state
const quizStateReducer = (state = 'quiz', action) => {
  switch (action.type) {
    case SET_QUIZ_STATE:
      return action.payload;
    default:
      return state;
  }
};

// Combine reducers
const rootReducer = combineReducers({
  gameState: gameStateReducer,
  quizState: quizStateReducer,
  // Add other reducers for different parts of your state if needed...
});

export default rootReducer;
