// reducers.js
import { START_QUIZ, NEXT_QUESTION, SUBMIT_ANSWER } from './actions';

const initialState = {
  questions: [], // Populate this with your set of questions
  currentQuestionIndex: 0,
  userAnswers: [],
  quizStarted: false,
};
const quizReducer = (state = initialState, action) => {
  switch (action.type) {
    case CHANGE_SCREEN:
      return {
        ...state,
        currentScreen: action.payload,
      };

    case START_QUIZ:
      return {
        ...state,
        quizStarted: true,
      };
    case NEXT_QUESTION:
      return {
        ...state,
        currentQuestionIndex: state.currentQuestionIndex + 1,
      };
    case SUBMIT_ANSWER:
      return {
        ...state,
        userAnswers: [...state.userAnswers, action.payload],
      };
    default:
      return state;
  }
};

export default quizReducer;
