export const SET_GAME_STATE = 'SET_GAME_STATE';
export const SET_QUIZ_STATE = 'SET_QUIZ_STATE';

export const setGameState = (newState) => ({
  type: SET_GAME_STATE,
  payload: newState,
});

export const setQuizState = (newState) => ({
  type: SET_QUIZ_STATE,
  payload: newState,
});

  export const setScore = (newScore) => ({
    type: 'SET_SCORE',
    payload: newScore,
  });
