// quizReducer.js
import { CHANGE_SCREEN } from './actions';

const initialState = {
  currentScreen: 'intro', // Set initial screen as 'intro'
};

const quizReducer = (state = initialState, action) => {
  switch (action.type) {
    case CHANGE_SCREEN:
      return {
        ...state,
        currentScreen: action.payload,
      };
    default:
      return state;
  }
};

export default quizReducer;
