import React from 'react';
import { useSelector } from 'react-redux';
import { connect } from 'react-redux';
import { changeScreen } from '../Redux/actions';

function ResultScreen({ score, changeScreen }) {
  // Get the score from the ScoreContext hook
  const quizScore = useSelector((state) => state.quizReducer);
  
  const handleRestart = () => {
    changeScreen('intro'); // Go back to the intro screen
  };

  return (
    <div className="result-screen">
      <h2>Result Screen</h2>
      <p>Final Score: {quizScore}</p>
      <button onClick={handleRestart}>Restart Quiz</button>
    </div>
  );
}

const mapStateToProps = (state) => ({
  quizScore: state.quiz.score,
});



const mapDispatchToProps = (dispatch) => ({
  changeScreen: (screen) => dispatch(changeScreen(screen)),
});

export default connect(mapStateToProps, mapDispatchToProps)(ResultScreen);
