import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { changeScreen, resetGame } from "../Redux/actions";

function ResultScreen() {
  const score = useSelector((state) => state.score); // Accessing score from Redux store
  const dispatch = useDispatch();

  return (
    <div className="result-screen">
      <h2>Result Screen</h2>
      <p>Final Score: {score}</p>
      // 
      <button onClick={() => dispatch(changeScreen("game"))}>PLay Game</button> 
      <button onClick={() => dispatch(resetGame())}>Restart Quiz</button>
    </div>
  );
}

export default ResultScreen;
